parameters.jsonBody:
={
  "input": {
    "bounds": {
      "geometry": {
        "type": "Point",
        "coordinates": [{{ $json.body.location.lng }}, {{ $json.body.location.lat }}]
      },
      "properties": {
        "crs": "http://www.opengis.net/def/crs/EPSG/0/4326"
      }
    },
    "data": [
      {
        "type": "sentinel-2-l2a",
        "dataFilter": {
          "timeRange": {
            "from": "{{ $now.minus({days: 30}).toISO() }}",
            "to": "{{ $now.toISO() }}"
          },
          "maxCloudCoverage": 30
        }
      }
    ]
  },
  "aggregation": {
    "timeRange": {
      "from": "{{ $now.minus({days: 30}).toISO() }}",
      "to": "{{ $now.toISO() }}"
    },
    "aggregationInterval": {
      "of": "P1D"
    },
    "evalscript": "//VERSION=3\nfunction setup() {\n  return {\n    input: [{ bands: ['B04', 'B08', 'dataMask'] }],\n    output: [{ id: 'ndvi', bands: 1 }]\n  };\n}\nfunction evaluatePixel(samples) {\n  let ndvi = (samples.B08 - samples.B04) / (samples.B08 + samples.B04);\n  return { ndvi: [ndvi] };\n}"
  }
}

parameters.jsCode:
// Process Sentinel Hub response and calculate farm health
const response = $input.first().json;
const location = $('Webhook').first().json.body.location;
const language = $('Webhook').first().json.body.language;

// Get latest NDVI value
let ndvi = 0.65; // Default if no data
let moisture = 'Normal';
let health = 'Good';
let trend = 'Stable';

if (response.data && response.data.length > 0) {
  const latestData = response.data[response.data.length - 1];
  if (latestData.outputs && latestData.outputs.ndvi) {
    ndvi = latestData.outputs.ndvi.bands.B0.stats.mean || 0.65;
  }
}

// Determine health status
if (ndvi > 0.6) {
  health = language === 'ar' ? 'جيدة' : 'Good';
  moisture = language === 'ar' ? 'طبيعية' : 'Normal';
} else if (ndvi > 0.4) {
  health = language === 'ar' ? 'متوسطة' : 'Fair';
  moisture = language === 'ar' ? 'منخفضة قليلاً' : 'Slightly Low';
} else {
  health = language === 'ar' ? 'تحتاج اهتمام' : 'Needs Attention';
  moisture = language === 'ar' ? 'منخفضة' : 'Low';
}

// Generate tip based on NDVI
let tip = '';
if (language === 'ar') {
  if (ndvi > 0.6) {
    tip = 'محاصيلك في حالة ممتازة! استمر في الري المنتظم.';
  } else if (ndvi > 0.4) {
    tip = 'قد تحتاج محاصيلك إلى مزيد من الري. راقب الأوراق بحثاً عن علامات الإجهاد.';
  } else {
    tip = 'تحتاج محاصيلك إلى اهتمام فوري. تحقق من الري والتسميد.';
  }
} else {
  if (ndvi > 0.6) {
    tip = 'Your crops are in excellent condition! Continue regular irrigation.';
  } else if (ndvi > 0.4) {
    tip = 'Your crops may need more water. Watch leaves for stress signs.';
  } else {
    tip = 'Your crops need immediate attention. Check irrigation and fertilization.';
  }
}

return {
  report: {
    ndvi: parseFloat(ndvi.toFixed(2)),
    moisture,
    health,
    trend,
    tip
  },
  location,
  language
};

